    for(int i = 0; i < SIZE; i++){
        arr[i] = i;
    }

    // Delete the array on the device
    #pragma acc delete(arr)

    // Check if the array is still in device memory
    #pragma acc parallel loop present(arr)
    for(int i = 0; i < SIZE; i++){
        if(arr[i] != i){
